class tassaActions extends sfActions
{
  public function executeIndex ()
  {
    return $this->forward('tassa', 'list');
  }

  public function executeList ()
  {
    $this->tassas = TassaPeer::doSelect(new Criteria());
  }

  /*public function executeShow ()
  {
    $this->tassa = TassaPeer::retrieveByPk($this->getRequestParameter('id'));
    $this->forward404Unless($this->tassa);
